import csv# import pandas as pd## def calculateAvgHwy(path):#     new_path = 'files/mpg.csv'#     df = pd.read_csv(new_path)#     new_df = df[['hwy','class']]#     mean_vals = new_df.groupby('class', as_index=False)['hwy'].mean()#     return mean_valspath = 'files/mpg.csv'def calculateAvgHwy(path):    with open(path) as csvfile:        reader = csv.DictReader(csvfile)        list1 = []        cl = set()        sdict = dict()        results = [None] * 7        for i in reader:            list1.append([i['hwy'], i['class']])            cl.add(i['class'])        for j in cl:            sdict.update({ j : [0,0]})        for k in list1:            tval = sdict[k[1]]            tval[0] += float(k[0])            tval[1] += 1            sdict.update({k[1] : tval})        for key in sdict:            sdict.update({key: sdict[key][0]/sdict[key][1]})        return sdictprint(calculateAvgHwy(path))